import requests
from bs4 import BeautifulSoup

URL = "http://virusshare.com/processlogin.4n6"
Username = "" #Enter virusshare username
Password = "" #Enter virusshare password

with open("MD5Hashes0-75.txt", "r") as md5HashesList: #Text file containing a list of md5hashes
	Hashes = []
	for line in md5HashesList:
		Hashes.append(line)
